    //清洗字符串，去除换行、空格
    static String[] cleanArray(String []array)
    {
        for (int i=0;i<array.length;i++)
            array[i]=array[i].trim().replace("\n","").replace("\r","");
        return array;
    }
    //检查是否有重复的
    static boolean SimgleLineDuplicateCheck(String line,String src)
    {
        return src.contains(line);
    }

